import request from "request";
require("dotenv").config();

const PAGE_ACCESS_TOKEN = process.env.PAGE_ACCESS_TOKEN;

const IMAGE_GET_STARTED = 'https://bit.ly/eric-bot1'

function callSendAPI(sender_psid, response) {
    // Construct the message body
    let request_body = {
      recipient: {
        id: sender_psid,
      },
      message: response,
    };
  
    // Send the HTTP request to the Messenger Platform
    request(
      {
        uri: "https://graph.facebook.com/v17.0/me/messages",
        qs: { access_token: PAGE_ACCESS_TOKEN },
        method: "POST",
        json: request_body,
      },
      (err, res, body) => {
        if (!err) {
          console.log("message sent!");
        } else {
          console.error("Unable to send message:" + err);
        }
      }
    );
  }

function getUserName(sender_psid, response) {
    return new Promise(function(resolve, reject) {
        // Send the HTTP request to the Messenger Platform
        request(
          {
            uri: `https://graph.facebook.com/${sender_psid}?fields=first_name,last_name,profile_pic&access_token=${PAGE_ACCESS_TOKEN}`,
            qs: { access_token: PAGE_ACCESS_TOKEN },
            method: "GET",
          },
          (err, res, body) => {
            console.log(body)
            if (!err) {
                body = JSON.parse(body)
                let username = `${body.last_name} ${body.first_name}`
                resolve(username)
            } else {
              console.error("Unable to send message:" + err);
              reject(err)
            }
          }
        );
    })
  }

let handleGetStarted = (sender_psid) => {
    return new Promise(async(resolve, reject) => {
        try {
            let username = await getUserName(sender_psid)
            let response1 = { text: `Cho mng ${username} n vi restaurant with VanHoa.` };
            let response2 = sendGetStartedTemplate()

            // send text message
            await callSendAPI(sender_psid, response1)

            // send generic template message
            await callSendAPI(sender_psid, response2)

            resolve('done')
        } catch (e) {
         reject(e);   
        }
    });
}

let sendGetStartedTemplate = () => {
  let response = {
    attachment: {
      type: "template",
      payload: {
        template_type: "generic",
        elements: [
          {
            title: "Nh hng VanHoa knh cho qu khch",
            subtitle: "Di y l cc la chn ca nh hng",
            image_url: IMAGE_GET_STARTED,
